sys_variable_value.value:
(function execute(inputs, outputs) {
  var obj = {};
  var allowedTables = ["incident", "problem", "sc_request"];
  if (allowedTables.indexOf(inputs.table_name) >= 0) {
    var gr = new GlideRecord(inputs.table_name);
    if (gr.get(inputs.record_id)) {
      for (var key in gr) {
        var val = gr.getValue(key);
        if (val) {
          obj[key] = val;
        }
      }
    }
  }
  var json = JSON.stringify(obj);  
  outputs.json = json;
})(inputs, outputs);

