sys_variable_value.value:
(function execute(inputs, outputs) {
  var gr = new GlideRecord(inputs.table_name);
  gr.get(inputs.record_id);
  var obj = {};
  for (var key in gr) {
    var val = gr.getValue(key);
    if (val) {
      obj[key] = val;
    }
  }
  var json = JSON.stringify(obj);  
  outputs.json = json;
})(inputs, outputs);

